    get friends(): V2NIMFriend[];
    get conversations(): V2NIMConversationForUI[];
    get localConversations(): V2NIMLocalConversationForUI[];
    get users(): V2NIMUser[];
    get teamList(): V2NIMTeam[];
    get applyMsgs(): V2NIMFriendAddApplicationForUI[];
